"use strict";var MovieApp=MovieApp||{url:"https://bananas-movie-reviews.herokuapp.com"};MovieApp.getMovies=function(){$.ajax({url:MovieApp.url+"/movies",type:"GET",dataType:"JSON"}).done(function(a){MovieApp.indexMovies(a)}).fail(function(a,b,c){console.log(a,b,c)})},MovieApp.indexMovies=function(a){console.log(a);var b=$("#results_template").html(),c=Handlebars.compile(b);$(".posts").html(c(a))},MovieApp.submitMovie=function(a){a.preventDefault&&a.preventDefault(),$.ajax({url:MovieApp.url+"/movies",type:"POST",dataType:"JSON",data:{movie:{title:$("input#movie-title").val(),gross:$("input#movie-gross").val(),release_date:$("input#movie-release-date").val(),mpaa_rating:$("input#movie-mpaa-rating").val(),description:$("textarea#movie-description").val()}}}).done(function(a){console.log(a),MovieApp.getMovies(),$("input#movie-title").val(""),$("input#movie-gross").val(""),$("input#movie-release-date").val(""),$("input#movie-mpaa-rating").val(""),$("textarea#movie-description").val("")}).fail(function(){console.log("error")})},MovieApp.toggleAddReview=function(){$("#toggle_add_review").click(function(){$("#new-review-form").toggle(300)})},$(document).ready(function(){MovieApp.getMovies(),MovieApp.toggleAddReview(),$("form#new-movie-form").on("submit",function(a){MovieApp.submitMovie(a)})});